import { combineReducers } from 'redux';

import axios from 'axios';

let bankInfo = {
    accounts:[],
    transaction:[]
};

let errorMessage = "";

console.log("test axios");
axios.get('http://my-json-server.typicode.com/bnissen24/project2DB/accounts')
    .then(response => {
        bankInfo.accounts = response.data ;
    }).catch(error => {
        errorMessage = error.message ;
    });

console.log(bankInfo);

const activityReducer = (state, action) => {
    let tempState = state.accounts;
    switch (action.type) {
        case 'DEPOSIT_MONEY':
            state.accounts[action.payload.accountId-1].balance += action.payload.amount;
            state.transaction.push(action.payload);
            return state;
        default:
            return bankInfo;
    }
}
export default combineReducers({
    bankInfo: activityReducer
});
