import React from 'react';
import { Link } from 'react-router-dom';


import axios from 'axios';

import AccountPage from './AccountPage';

class AccountList extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            accountList: [],
        };
        this.getData = this.getData.bind(this);
    }
    componentDidMount(){
        this.getData();
    }

    getData() {
        axios.get('http://my-json-server.typicode.com/bnissen24/project2DB/accounts')
          .then(response => {
            this.setState({ accountList: response.data });
          }).catch(error => {
            this.setState({ errorMessage: error.message });
          });
    }

    render(){
        console.log(this.state.accountList);
        let renderedList = this.state.accountList.map((account)=>{
            return(
                <tr key={account._id}>
                    <td>{account._id}</td>
                    <td>{account.name}</td>
                    <td>${account.balance}</td>
                    <td>
                    <Link
                        className={`/page/${account._id}`} to={`/page/${account._id}`}>
                        Link
                    </Link>

                    </td>
                </tr>
            )
        })

        return(
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Name</th>
                        <th scope="col">Balance</th>
                        <th scope="col">Account Page</th>
                    </tr>
                </thead>
                <tbody>
                    {renderedList}
                </tbody>

            </table>      
        )
    }
}

export default AccountList;
