import React from 'react';
import { connect } from 'react-redux';
import {fetchData} from "../actions";
import {receivedData} from "../actions"

class AccountPage extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            accountName: this.props.match.params.name,
            accountInfo: this.props.location.state
        };
    }
  goToHome = () => {
    this.props.history.push(`/`);
  }
  render() {
    console.log("test")
    console.log(this.state.accountInfo)
    let accountInfo = this.state.accountInfo;

    return (
      <div className="page-variable">
        <h3>Account Page: { this.state.accountName}</h3>
        <button onClick={this.goToHome}>Go To Home</button>
        <div className="container" style={{ marginTop: '15px' }}>
            <div className="row">
                <div className="col-sm-4">
                    <div className="card" >
                        <div className="card-body">
                            <h5 className="card-title">Account Info</h5>
                        </div>
                        <ul className="list-group list-group-flush">
                            <li className="list-group-item">Id: {accountInfo.data._id}</li>
                            <li className="list-group-item">Name: {accountInfo.data.name}</li>
                            <li className="list-group-item">Balance: {accountInfo.data.balance}</li>
                        </ul>
                    </div>
                </div>
                <div className="col-sm-4">
                    Deposit

                </div>
                <div className="col-sm-4">
                    {this.props.fetchData}
                    {this.props.receivedData}
                </div>
            </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
    return {
      accounts: state.bankInfo.accounts,
      transaction: state.bankInfo.transaction
    };
  };
  export default connect(mapStateToProps, {fetchData},{receivedData})(AccountPage);
