import { combineReducers } from 'redux';

import axios from 'axios';

let bankInfo = {
    accounts:[],
    transaction:[]
};
function getData(){
    let data = [];
    console.log("test axios");
    axios.get('http://my-json-server.typicode.com/bnissen24/project2DB/accounts')
        .then(response => {
            data = response.data;
        }).catch(error => {
            console.log("fail axios");
    });
    return data;
}

bankInfo.accounts = getData();
console.log(bankInfo.accounts);

const activityReducer = (state, action) => {
    switch (action.type) {
        case 'DEPOSIT_MONEY':
            state.accounts[action.payload.accountId-1].balance += action.payload.amount;
            state.transaction.push(action.payload);
            return state;
        default:
            return bankInfo;
    }
}
export default combineReducers({
    bankInfo: activityReducer
});
