import { combineReducers } from 'redux';

import axios from 'axios';

let bankInfo = {
    accounts:[],
    transaction:[]
};


const activityReducer = (state, action) => {
    switch (action.type) {
        case 'RECEIVED_DATA':
            state.accounts = action.data
        case 'DEPOSIT_MONEY':
            state.accounts[action.payload.accountId-1].balance += action.payload.amount;
            state.transaction.push(action.payload);
            return state;
        default:
            return bankInfo;
    }
}
export default combineReducers({
    bankInfo: activityReducer
});
